mapper.update:
UPDATE download_task
task_name = #{taskName,jdbcType=VARCHAR}, file_name = #{fileName,jdbcType=VARCHAR}, file_url = #{fileUrl,jdbcType=VARCHAR}, task_status = #{taskStatus,jdbcType=INTEGER}, total_count = #{totalCount,jdbcType=BIGINT}, processed_count = #{processedCount,jdbcType=BIGINT}, error_message = #{errorMessage,jdbcType=LONGVARCHAR}, create_user = #{createUser,jdbcType=VARCHAR}, create_time = #{createTime,jdbcType=TIMESTAMP}, complete_time = #{completeTime,jdbcType=TIMESTAMP},
WHERE id = #{id,jdbcType=BIGINT}

mapper.select:
SELECT 
FROM download_task


        AND create_user = #{createUser,jdbcType=VARCHAR}


        AND task_status = #{status,jdbcType=INTEGER}


ORDER BY create_time DESC

    LIMIT #{limit}

mapper.update:
UPDATE download_task
SET task_status = #{status,jdbcType=INTEGER}
WHERE id IN

    #{taskId}

UPDATE download_task
SET processed_count = #{processedCount,jdbcType=BIGINT}
WHERE id = #{taskId,jdbcType=BIGINT}

UPDATE download_task
SET task_status = #{status,jdbcType=INTEGER},
    complete_time = NOW(),
    file_url = #{fileUrl,jdbcType=VARCHAR},
    error_message = #{errorMessage,jdbcType=LONGVARCHAR}
WHERE id = #{taskId,jdbcType=BIGINT}
