mapper.update:
UPDATE load_test_order
order_id = #{orderId,jdbcType=BIGINT}, buyer_id = #{buyerId,jdbcType=BIGINT}, current_status = #{currentStatus,jdbcType=TINYINT}, business_line_id = #{businessLineId,jdbcType=BIGINT}, update_time = NOW()
WHERE id = #{id,jdbcType=BIGINT}

UPDATE load_test_order 
SET current_status = #{currentStatus,jdbcType=INTEGER}, update_time = NOW()
WHERE order_id = #{orderId,jdbcType=BIGINT}

mapper.select:
SELECT 
FROM load_test_order


        AND buyer_id = #{buyerId,jdbcType=BIGINT}


        AND current_status = #{currentStatus,jdbcType=INTEGER}


        AND business_line_id = #{businessLineId,jdbcType=INTEGER}


        AND create_time >= #{startTime,jdbcType=TIMESTAMP}


        AND create_time <= #{endTime,jdbcType=TIMESTAMP}


ORDER BY create_time DESC

SELECT id, order_id, buyer_id, current_status, business_line_id, create_time, update_time FROM load_test_order
AND DATE(create_time) >= #{startTime,jdbcType=VARCHAR}
AND DATE(create_time) <= #{endTime,jdbcType=VARCHAR}
AND current_status = #{currentStatus,jdbcType=INTEGER}
AND business_line_id = #{businessLineId,jdbcType=BIGINT}
ORDER BY create_time DESC
