mapper.update:
UPDATE red_envelope_record
uid = #{uid,jdbcType=BIGINT}, red_envelope_id = #{redEnvelopeId,jdbcType=BIGINT}, receive_time = #{receiveTime,jdbcType=BIGINT}, batch_id = #{batchId,jdbcType=BIGINT}, amount = #{amount,jdbcType=BIGINT}, status = #{status,jdbcType=INTEGER}, platform_type = #{platformType,jdbcType=INTEGER}, update_time = NOW()
WHERE receive_id = #{receiveId,jdbcType=BIGINT}

mapper.select:
SELECT 
FROM red_envelope_record


        AND batch_id = #{batchId,jdbcType=BIGINT}


        AND red_envelope_id = #{redEnvelopeId,jdbcType=BIGINT}


        AND uid = #{uid,jdbcType=BIGINT}


        AND status = #{status,jdbcType=INTEGER}


        AND platform_type = #{platformType,jdbcType=INTEGER}


        AND receive_time >= #{startTime,jdbcType=BIGINT}


        AND receive_time <= #{endTime,jdbcType=BIGINT}


ORDER BY create_time DESC

    LIMIT #{pageSize,jdbcType=INTEGER}

SELECT receive_id, uid, red_envelope_id, receive_time, batch_id, amount, status, platform_type, create_time, update_time FROM red_envelope_record
AND batch_id = #{batchId,jdbcType=BIGINT}
AND status = #{status,jdbcType=INTEGER}
AND platform_type = #{platformType,jdbcType=INTEGER}
ORDER BY create_time DESC
