mapper.update:
UPDATE test_user
user_status = #{userStatus,jdbcType=INTEGER}, user_type = #{userType,jdbcType=INTEGER}, address_id = #{addressId,jdbcType=BIGINT}, ppu = #{ppu,jdbcType=VARCHAR}, platform_b_uid = #{platformBUid,jdbcType=BIGINT}, update_time = NOW()
WHERE uid = #{uid,jdbcType=BIGINT}

mapper.select:
SELECT uid, user_type, user_status, address_id, ppu, platform_b_uid, create_time, update_time FROM test_user
AND user_type = #{userType,jdbcType=INTEGER}
AND user_status = #{userStatus,jdbcType=INTEGER}
ORDER BY create_time DESC

SELECT user_type, user_status, COUNT(*) as count, SUM(CASE WHEN ppu IS NOT NULL AND ppu != '' THEN 1 ELSE 0 END) as has_ppu_count, SUM(CASE WHEN address_id IS NOT NULL THEN 1 ELSE 0 END) as has_address_count, SUM(CASE WHEN platform_b_uid IS NOT NULL THEN 1 ELSE 0 END) as platform_b_count FROM test_user
AND user_type = #{userType,jdbcType=INTEGER}
GROUP BY user_type, user_status ORDER BY user_type, user_status
